pragma solidity ^0.5.16;

import "./ERC777.sol";

interface Lendee {
    function doFlashLoan() external;
}

contract WrappedETCToken is ERC777 {
    using SafeMath for uint256;

    event Deposit(address indexed _owner, uint _value);
    event Withdrawal(address indexed _owner, uint _value);
    event FlashLoan(address indexed _owner, uint _value);

    constructor () public ERC777("WrappedETC", "WETC", new address[](0)) {
    }

    function() external payable {
        deposit();
    }

    function deposit() public payable {
        super._mint(msg.sender, msg.sender, msg.value, "", "");
        emit Deposit(msg.sender, msg.value);
    }

    function withdraw(uint256 wad) public {
        super._burn(msg.sender, msg.sender, wad, "", "");
        msg.sender.transfer(wad);
        emit Withdrawal(msg.sender, wad);
    }
    function flash(uint256 amount) public {
        super._mint(msg.sender, msg.sender, amount, "", "");
        Lendee(msg.sender).doFlashLoan();
        super._burn(msg.sender, msg.sender, amount, "", "");
        emit FlashLoan(msg.sender, amount);
    }
}
